create table author (id bigint generated by default as identity, full_name varchar(255), primary key (id));
create table book (id bigint generated by default as identity, title varchar(255), primary key (id));
create table book_author (author_id bigint not null, book_id bigint not null);
create table book_genre (book_id bigint not null, genres_id bigint not null);
create table comment (book_id bigint, id bigint generated by default as identity, comment varchar(1000) not null, primary key (id));
create table genre (id bigint generated by default as identity, genre_name varchar(255) not null, primary key (id));
